import logo from './logo.svg';
import './App.css';

function App() {
  const [text, setText] = React.useEffect('');

  const handleClick = (e) => {
    setText(e.target.value);
  };

  return (
    <div className='App'>
      <input
        type='text'
        placeholder='Enter Item'
        value={text}
        onChange={(e) => {
          e.target.value;
        }}
      />
      <button onClick={handleClick}>Add Item</button>
    </div>
  );
}

export default App;
