import React from 'react';

function List({ listItems }) {
  const deleteItem = (value) => {
    listItems.filter((item) => {
      return item != value;
    });
  };

  return (
    <div className='list-items'>
      <ul>
        {listItems.map((item, idx) => (
          <li key={idx} onClick={() => deleteItem(item)}>
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default List;
